'use strict';

import mysql from 'mysql';

export default class Connection {

    constructor (handle) {
        this.max_retries = 3;
        this.handle = handle;
        this.retries = 0;
        this.connect();
    }

    connect () {
        const handle = this.handle;
        const key = handle._key;
        let connection;

        if (handle[key].is_pool) {
            handle._logger.info('Added a pool connection for', key);
            connection = mysql.createPool(handle[key].config);
        }
        else {
            handle._logger.log('Created a single connection');
            connection = mysql.createConnection(handle[key].config);
        }

        handle[key].connection = connection;
        handle.current_connection = handle[key].connection;

        this.recon = this.recon.bind(this);

        connection.on('close', this.recon);
        connection.on('error', this.recon);

        // this.keepAlive();
        return this;
    }

    recon (err) {
        this.handle._logger.error(
            this.handle.is_pool
                ? 'pool'
                : 'single',
            'connection error',
            err
        );

        if (++this.retries >= this.max_retries) {
            this.handle._logger.error('too many errors');
            return;
        }

        this.connect();
        return this;
    }

    keep_alive () {
        setInterval(() => {
            this.handle.query('SELECT 1;', (err) => {
                if (err) {
                    this.handle._logger.error(err);
                }
            });
        }, 5000);

        return this;
    }

    end () {
        if (this.handle.current_connection) {
            this.handle.current_connection.end();
        }

        return this;
    }
}

'use strict';

import Transaction from './Transaction';
import Connection from './Connection';
import Query from './Query';
import mysql from 'mysql';

/**
 * This is my class description
 */
export default class CustomMySQL {

    constructor () {
        this.escape = mysql.escape;
        this._logger = console;
        this._max_retry = 3;
    }


    set_logger (logger) {
        this._logger = logger || console;
        return this;
    }

    set_max_retry (max) {
        this._max_retry = max || 3;
        return this;
    }


    add (key, config, is_pool) {
        if (!key || !config) {
            throw new Error('key or config is missing');
        }

        if (typeof key !== 'string') {
            throw new Error('key should be a string');
        }

        if (typeof config !== 'object') {
            throw new Error('config should be an object');
        }

        this._key = key;
        this[key] = {config};
        this[key].is_pool = !!is_pool;

        if (is_pool) {
            new Connection(this);
        }

        return this;
    }

    retry_if (retryable_errors) {
        this.retryable_errors = retryable_errors;
        return this;
    }

    use (key) {
        if (!key) {
            throw new Error('key is missing');
        }

        if (!this[key]) {
            throw new Error('Key does not exist. Add a connection first by using mysql.add(key, config, is_pool)');
        }

        this._key = key;
        this.retryable_errors = null;

        if (!this.is_pool && this.current_connection) {
            this.end();
        }

        return this;
    }

    args () {
        if (!this._key) {
            throw new Error('Key does not exist. Add a connection first by using mysql.add(key, config, is_pool)');
        }

        this._args = arguments;
        return this;
    }

    query () {
        if (!this._key) {
            throw new Error('Key does not exist. Add a connection first by using mysql.add(key, config, is_pool)');
        }

        if (arguments.length < 2) {
            throw new Error('Incomplete arguments. Have at least a query and a callback');
        }

        if (typeof arguments[0] !== 'string') {
            throw new Error('Query is not a string');
        }

        if (typeof arguments[arguments.length - 1] !== 'function') {
            throw new Error('Last parameter is not a function');
        }

        new Query(this, ...arguments);
        return this;
    }

    transaction () {
        if (!this.current_connection) {
            new Connection(this);
        }

        return new Transaction(this);
    }

    end () {
        if (this._key && !this[this._key].is_pool && this[this._key].connection) {
            this[this._key].connection.end();
            this.current_connection = null;
            this[this._key].connection = null;
        }

        return this;
    }



    /* Everything below will be deprecated */

    open (config) {
        let self = this,
            config_str = '',
            i;


        for (i in config) {
            config_str += config[i];
        }


        this._key = config_str;

        if (this[config_str] && this[config_str].connection) {
            return this;
        }

        this[config_str] = {
            config,
            is_pool: true,
            connection: mysql.createPool(config)
        };

        this[config_str].connection.on('error', function (err) {
            console.log('error', err);
        });

        this[config_str].connection.on('close', function (err) {
            console.log('close', err);
            self[config_str].connection = mysql.createPool(self[config_str].config);
        });

        this.escapeId = this[config_str].connection.escapeId.bind(this[config_str].connection);

        return this;
    }

    async (query, args, async_args, collector, fn) {
        let results = [];
        let len = args.length;

        function _collector (err, result, _args) {
            let temp = {
                err: err,
                result: result,
                args: _args
            };

            results.push(
                collector
                ? collector(err, result, _args)
               : temp
            );

            if (!--len) {
                fn(async_args || results);
            }
        }

        if (arguments.length === 4) {
            fn = collector;
            collector = async_args;
            async_args = null;
        }

        if (arguments.length === 3) {
            fn = async_args;
            async_args = null;
        }

        args.forEach((arg, index) => {
            this.args(async_args && async_args.hasOwnProperty(index)
                    ? async_args[index]
                   : arg
                )
                .query(query, arg, _collector);
        });

        return this;
    }

    on (_event, cb) {
        if (!this._key) {
            throw new Error('Key does not exist. Add a connection first by using mysql.add(key, config, is_pool)');
        }

        return this[this._key].connection.on(_event, cb);
    }
}

'use strict';

import Connection from './Connection';
import mysql from 'mysql';

export default class Query {

    constructor (mysql) {
        const args = Array.from(arguments);

        this.previous_errors = [];
        this.mysql = mysql;
        this.retries = 0;

        args.shift();

        this.query(...args);
    }

    query () {
        const _args = Array.from(arguments);
        const mysql_handler = this.mysql;
        const last_query = arguments[0];

        let len = arguments.length;
        let connection;
        let cb;

        function new_callback (err, result) {

            // if retryable, re-try
            if (err && mysql_handler.retryable_errors && ~mysql_handler.retryable_errors.indexOf(err.code)) {
                this.retries++;

                this.previous_errors.push(JSON.parse(JSON.stringify(err)));

                if (this.retries === mysql_handler._max_retry) {

                    return cb(
                        {
                            message: 'Reached max retries',
                            code: 'ER_MAX_RETRIES',
                            max_tries: mysql_handler._max_retry,
                            previous_errors: this.previous_errors
                        },
                        null,
                        mysql_handler._args,
                        last_query
                    );
                }

                return this.query(..._args);
            }

            // call callback
            cb(err, result, mysql_handler._args, last_query);
        }

        while (len--) {
            if (typeof arguments[len] === 'function') {
                // get callback
                cb = arguments[len];

                // replace callback
                arguments[len] = new_callback.bind(this);
                break;
            }
        }


        if (!mysql_handler.current_connection) {
            new Connection(mysql_handler);
        }

        mysql_handler
            .current_connection
            .query
            .apply(mysql_handler.current_connection, arguments);
    }
}

'use strict';

export default class Transaction {

    constructor (mysql) {
        this.connection = mysql.current_connection;
        this.mysql = mysql;
        this.queries = [];
        this.errors = [];
        this.retries = 0;
    }

    query () {
        if (arguments.length < 2) {
            throw new Error('Too few arguments. Must at least have query and callback');
        }

        this.queries.push(Array.from(arguments));
        return this;
    }

    run_queries (err) {
        const current_query = this.queries.shift();
        const last_query = current_query && current_query[0];

        if (typeof current_query === 'undefined') {
            return this.connection.commit((err) => {
                this.final_callback(err, null, this.connection);
            });
        }

        function custom_cb (err, result) {
            // if retryable, re-try
            this.current_cb(err, result, this.mysql._args, last_query);


            if (err) {
                return this.connection.rollback(() => {
                    this.final_callback(err, result, this.mysql._args, last_query);
                });
            }

            // neext
            this.run_queries();
        }

        if (err) {
            return this.final_callback({message: 'Error in creating transaction'});
        }

        this.current_cb = current_query.pop();

        current_query.push(custom_cb.bind(this))

        this.connection.query.apply(this.connection, current_query);
    }

    commit (cb) {
        this.final_callback = cb;
        this.connection.beginTransaction(this.run_queries.bind(this));
        return this.mysql;
    }
}
